Scripts.OnEnter:
GameTooltip:SetOwner(AuctionFrame, "ANCHOR_NONE");
GameTooltip:SetPoint("BOTTOMLEFT", AuctionFrame, "BOTTOMLEFT", 190, 40);
GameTooltip:SetText(MARKETWATCHER_TOOLTIP7, nil, nil, nil, nil, 1);

Scripts.OnClick:
PlaySound(SOUNDKIT.IG_MAINMENU_OPTION_CHECKBOX_ON);
local itemId = MarketWatcher.History.itemSelected;
if ( not itemId ) then
	return
end
MarketWatcher.History.scan = MarketWatcher.GetNumScans(itemId);
MarketWatcherHistoryFrameListScrollFrameScrollBar:SetValue(0);
MarketWatcher.History.Browse_Update();
